--  DDL for View SELECT_LAST_USERS
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SELECT_LAST_USERS" ("POST_ID", "USER_ID", "LATEST_USER_ID", "LATEST_USER_DISPLAY_NAME") AS 
  Select Post_ID, USER_ID,
(SELECT LISTAGG(USER_ID,',')WITHIN GROUP(ORDER BY POST_ID desc) "user_id" FROM SELECT_TOP_POSTS WHERE SELECT_TOP_POSTS.PARENT_POST_ID= P.Post_ID) "LATEST_USER_ID",
(SELECT LISTAGG(DISPLAY_NAME,',')WITHIN GROUP(ORDER BY POST_ID desc) "DISPLAY_NAME" FROM SELECT_TOP_POSTS WHERE SELECT_TOP_POSTS.PARENT_POST_ID= P.Post_ID) "LATEST_USER_DISPLAY_NAME"
FROM POSTS P
WHERE POST_TYPE_ID =1;
--------------------------------------------------------
--  DDL for View SELECT_POSTS
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SELECT_POSTS" ("POST_ID", "USER_ID", "NAME1", "TITLE", "TAG_TYPE_ID", "TAG_TYPE", "BODY_CONTENT", "CREATED_DATE", "LAST_EDITED_DATE", "CLOSED_DATE", "VIEW_COUNT", "COMMENTCOUNT", "FAVORITE_COUNT", "LATEST_USER_ID", "LATEST_USER_DISPLAY_NAME", "ACTIVITY") AS 
  SELECT P.Post_ID
     , P.USER_ID
     , U.NAME1
     , TITLE
     , TT.TAG_TYPE_ID
     , TT.TAG_TYPE
     , P.BODY_CONTENT
     , P.CREATED_DATE
     , P.LAST_EDITED_DATE
     , CLOSED_DATE
     , NVL(VIEW_COUNT,0) VIEW_COUNT
     , NVL(COMMENTCOUNT,0) COMMENTCOUNT
     , NVL(FAVORITE_COUNT,0) FAVORITE_COUNT
     , NVL(SLU.LATEST_USER_ID,P.USER_ID) LATEST_USER_ID
     , NVL(SLU.LATEST_USER_DISPLAY_NAME,U.DISPLAY_NAME) LATEST_USER_DISPLAY_NAME  
     , ((TO_DATE(SYSDATE,'dd-mon-rrrr')-P.CREATED_DATE)||' Days') ACTIVITY
     FROM POSTS P
JOIN SELECT_LAST_USERS SLU ON SLU.POST_ID=P.Post_ID
JOIN TAGTYPES TT ON TT.TAG_TYPE_ID=P.TAG_TYPE_ID
JOIN USERSDETAILS U ON U.USER_ID=P.USER_ID
ORDER BY P.LAST_EDITED_DATE, P.CREATED_DATE, P.Post_ID Desc;
