--  DDL for View SELECT_POST_VOTE
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SELECT_POST_VOTE" ("POST_ID", "UPVOTE", "DOWNVOTE") AS 
  SELECT VS.POST_ID
    , (SELECT COUNT(VOTE_ID) UPVOTE
              FROM VOTES VU 
              WHERE VOTE_TYPE_ID=1 
              AND VU.POST_ID = VS.POST_ID ) UPVOTE
    ,(SELECT COUNT(VOTE_ID) UPVOTE
             FROM VOTES VD 
             WHERE VOTE_TYPE_ID=2 
             AND VD.POST_ID = VS.POST_ID ) DOWNVOTE 
FROM VOTES VS
GROUP BY VS.POST_ID;
