--  DDL for View SELECT_TOP_POSTS
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SELECT_TOP_POSTS" ("POST_ID", "PARENT_POST_ID", "USER_ID", "DISPLAY_NAME", "POST_TYPE_ID", "NUM") AS 
  select "POST_ID","PARENT_POST_ID","USER_ID","DISPLAY_NAME","POST_TYPE_ID","NUM" 
from (select Post_id, PARENT_POST_ID,a.User_id,DISPLAY_NAME,Post_type_id, row_number() over (PARTITION by PARENT_POST_ID order by Post_id desc) as num 
        from POSTS a 
        JOIN USERSDETAILS U ON U.USER_ID =a.USER_ID
        
        where PARENT_POST_ID in 
(select Post_id from Posts where Post_type_id=1)
)
where num < 4;
