--  DDL for Procedure PROC_SINGLE_POST_SELECT
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PROC_SINGLE_POST_SELECT" 
(
  P_POST_ID         IN NUMBER 
, P_CUR      out SYS_REFCURSOR
) AS 
BEGIN
UPDATE POSTS SET 
        VIEW_COUNT = NVL(VIEW_COUNT,0) + 1
        WHERE POST_ID=P_POST_ID;
OPEN P_CUR FOR
  SELECT PS.POST_ID
     , PS.PARENT_POST_ID
     , PS.SUB_PARENT_POST_ID
     , PS.USER_ID
     , (US.NAME1 ||''|| US.NAME2) AS NAME1 
     , US.DISPLAY_NAME
     , NVL(PS.LAST_EDITOR_DISPLAY_NAME,DISPLAY_NAME) LAST_EDITOR_DISPLAY_NAME 
     , PS.POST_TYPE_ID
     , PT.POST_TYPE
     , PS.TITLE
     , PS.TAG_TYPE_ID
     , TT.TAG_TYPE
     , PS.SUB_TAGS
     , TO_DATE(PS.CREATED_DATE,'dd mon, rrrr') CREATED_DATE
     , NVL(PS.BODY_CONTENT,0) BODY_CONTENT
     , NVL(PS.ANSWER_COUNT,0) ANSWER_COUNT
     , NVL(PS.COMMENTCOUNT,0) COMMENTCOUNT
     , NVL(PS.FAVORITE_COUNT,0) FAVORITE_COUNT
     , NVL(PS.VIEW_COUNT,0) VIEW_COUNT
     , PS.FILE_NAME
     , PS.CLOSED_DATE
     , PS.CLOSED_BY
     , NVL(VU.UPVOTE,0) UPVOTE
     , NVL(VU.DOWNVOTE,0) DOWNVOTE
     FROM POSTS PS
    JOIN POSTTYPES PT ON PT.POST_TYPE_ID = PS.POST_TYPE_ID
    JOIN TAGTYPES TT ON TT.TAG_TYPE_ID = PS.TAG_TYPE_ID
    JOIN USERSDETAILS US ON US.USER_ID = PS.USER_ID
    LEFT JOIN SELECT_POST_VOTE VU ON VU.POST_ID = PS.POST_ID
    WHERE PS.POST_ID = P_POST_ID
    OR PS.PARENT_POST_ID = P_POST_ID
    ORDER BY PS.POST_TYPE_ID, PS.POST_ID; 
END PROC_SINGLE_POST_SELECT;

